var b = require('batterie');
var eq = require('.');
b.describe('eq', function () {
    b.expect(eq).toBeFunction();
    b.it('needs to have 2 objects with the push command on them', function (t) {
        t.expect(function () {
            eq({}, {});
        }).toThrow();
        t.expect(function () {
            eq({}, {}, [], []);
        }).notToThrow();
    }, 2);
    b.it('checks deep equality', function (t) {
        t.expect(eq({
            a: {
                b: 1
            }
        }, {
            a: {
                b: 1
            }
        }, [], [])).toBeTrue();
    });
});