var join = require('.');
var b = require('batterie');
var numbers = [31, 2, 56];
b.describe('join', function () {
    b.it('a normalizer for the join method', [
        ['by default it joins by commas', join(numbers), '31,2,56'],
        ['but can also join with other delimiters', join(numbers, '.'), '31.2.56']
    ]);
});