var withinRange = require('number/within-range');
var parsetime = require('time/parse');
module.exports = isDateTime;

function isDateTime(value) {
    var dt = parsetime(new Date(value));
    return !!(dt.year) && withinRange(dt.month, 1, 12) && withinRange(dt.date, 1, 31) && withinRange(dt.hour, 0, 23) && withinRange(dt.minute, 0, 59) && withinRange(dt.second, 0, 59) && withinRange(dt.ms, 0, 999) && withinRange(dt.zone, -12, 12);
}