var at = require('.');
var b = require('batterie');
b.describe('at', function () {
    b.expect(at).toBeFunction();
    b.expect(at()).toBeUndefined();
    b.expect(at({
        a: {
            b: false
        }
    }, 'a.b')).toBeFalse();
    b.expect(at({
        a: {
            b: [{
                c: false
            }]
        }
    }, 'a.b[0].c')).toBeFalse();
});