var fromPairs = require('.');
var b = require('batterie');
b.describe('fromPairs', function () {
    b.expect(fromPairs).toBeFunction();
    b.expect(fromPairs()).toEqual({});
    b.expect(fromPairs([
        ['a', 'b'],
        ['c', 'd'],
        ['e', 1],
        [2, 'f']
    ])).toEqual({
        a: 'b',
        c: 'd',
        e: 1,
        '2': 'f'
    });
});