module.exports = get;
var toPath = require('to/path');
var isArray = require('is/array');
var forEachEnd = require('array/base/for-each-end');
var isNil = require('is/nil');
var u;

function get(target_, path) {
    var target = target_;
    if (!target) {
        return u;
    }
    var p = isArray(path) ? path : toPath(path);
    var length = p.length;
    if (length > 1) {
        forEachEnd(p, function traverser(key) {
            if (isNil(target)) {
                target = u;
                return true;
            }
            target = target[key];
        });
    } else if (length) {
        target = target[p[0]];
    } else {
        target = u;
    }
    return target;
}