var get = require('.');
var b = require('batterie');
b.describe('get', function () {
    b.expect(get).toBeFunction();
    b.expect(get()).toBeUndefined();
    b.expect(get(false)).toBeUndefined();
    b.expect(get(null)).toBeUndefined();
    b.expect(get({})).toBeUndefined();
    var special = {};
    var specialExpectation = b.expect(get(special)).toBeUndefined();
});