module.exports = merge;
var shallowMergeWithCustomizer = require('object/merge/with-shallow-customizer');
var deepMergeWithCustomizer = require('object/merge/with-deep-customizer');
var mergeWith = require('object/merge/with');

function merge(obj1, obj2, deep) {
    return mergeWith(obj1, obj2, deep ? deepMergeWithCustomizer : shallowMergeWithCustomizer);
}