var b = require('batterie');
var toString = require('.');
b.describe('toString', function () {
    b.expect(toString).toBeFunction();
    b.it('converts values to an object class notation', function (t) {
        t.expect(toString()).toBe('[object Undefined]');
    });
    b.it('operates on whatever context it is running in', function (t) {
        t.expect(toString.call(null)).toBe('[object Null]');
        t.expect(toString.call([])).toBe('[object Array]');
    }, 2);
});