var b = require('batterie');
var escape = require('.');
b.describe('string/escape', function () {
    b.expect(escape).toBeFunction();
    b.expect(escape).toReturnString();
    b.it('replaces html', function (t) {
        t.expect(escape('<div></div>')).toEqual('&lt;div&gt;&lt;/div&gt;');
    });
});