var booleans = {
    true: true,
    false: false
};
var castBoolean = require('@timelaps/hacks/to-boolean');
module.exports = function toBoolean(thing) {
    var converted = (thing + '').trim();
    if (booleans.hasOwnProperty(converted)) {
        return booleans[converted];
    }
    return castBoolean(thing);
};