var booleans = {
    true: true,
    false: false
};
var has = require('object/has');
var castBoolean = require('boolean/cast');
module.exports = function toBoolean(thing) {
    var converted = (thing + '').trim();
    if (has(booleans, converted)) {
        return booleans[converted];
    }
    return castBoolean(thing);
};