var floatToInteger = require('number/float-to-integer');
var isNan = require('is/nan');
module.exports = function (number, notSafe) {
    var converted;
    return floatToInteger((converted = toNumber(number)) == number ? (notSafe ? safeInteger(converted) : converted) : isNan(converted) ? 0 : converted);
};