var toNumber = require('to/number');
var b = require('@timelaps/batterie');
b.describe('to.number', function () {
    b.expect(toNumber).toBeFunction();
    b.expect(toNumber()).toBeNan();
    b.expect(toNumber('5')).toBe(5);
    b.expect(toNumber({
        valueOf: function () {
            return '10';
        }
    })).toBe(10);
});